package intro;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 7; //primitive
		double b = 1.25; // non primitive
		double x = (a/b);
		String s = "semih";
		char c = 'c';
		boolean z = true;
		
		System.out.println(a +" "+ "is an integer and we called as primitive value");
		System.out.println(b +" "+ "is a double");
		System.out.println(x +" "+ "is a double");
		System.out.println(s +" "+ "is string and we called as non primitive value");
		System.out.println(c +" "+ "is a character");
		System.out.println(z +" "+ "is boolean that means true or false");
	
		
	}

}
